import React, { Component } from "react";
import Scrollchor from "react-scrollchor";
import Home from './Home';
import About from './About';
import MyProjects from './MyProjects';
import Contact from './Contact';
import './css/Navigation.css';

class Navigation extends Component {
 render() {
  return (
    <div>
      <nav id="top">
        <label for="show-menu" className="show-menu"><i class="fas fa-bars"></i>&nbsp; Menu</label>
        <input type="checkbox" id="show-menu" />

        <ul className="navigation">
        <h2 className="logo">{this.props.logoTitle}</h2>
          <li><Scrollchor to="one" >Home</Scrollchor></li>
          <li><Scrollchor to="two" >About</Scrollchor></li>
          <li><Scrollchor to="three" >My Projects</Scrollchor></li>
          <li><Scrollchor to="four" >Contact</Scrollchor></li>
        </ul>

      </nav>

      <div id="one"><Home title="Hi, my name is Ivan and i'm a web developer." button="Find Out More"/></div>
      <div id="two"><About /></div>
      <div id="three"><MyProjects /></div>
      <div id="four"><Contact /></div>

    </div>
    );
  }
}

export default Navigation;
